// src/theme.js
import { createTheme } from "@mui/material/styles";
import "@fontsource/poppins";      // Texte
import "@fontsource/roboto-slab";  // Titres

const theme = createTheme({
  palette: {
    mode: "light",
    background: {
      default: "#F3F4F6",    // Gris clair apaisant
      paper: "#FFFFFF",      // Blanc pour les cartes/éléments
    },
    text: {
      primary: "#2E2E2E",    // Gris très foncé
      secondary: "#6B7280",  // Gris doux
    },
    primary: {
      main: "#4B5563",       // Gris bleuté élégant (ex. Tailwind slate-600)
    },
    secondary: {
      main: "#9CA3AF",       // Gris neutre plus clair
    },
    accent: {
      main: "#D97706",       // Orange doux discret pour petits accents
    },
  },
  typography: {
    fontFamily: "'Poppins', sans-serif",
    button: {
      textTransform: "none",
      fontWeight: "bold",
    },
    h1: {
      fontFamily: "'Roboto Slab', serif",
      fontSize: "2.5rem",
      fontWeight: 700,
      color: "#2E2E2E",
    },
    h2: {
      fontFamily: "'Roboto Slab', serif",
      fontSize: "2rem",
      fontWeight: 600,
      color: "#4B5563",
    },
    body1: {
      fontSize: "1rem",
      color: "#2E2E2E",
    },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          backgroundColor: "#4B5563",
          color: "#FFFFFF",
          borderRadius: 8,
          boxShadow: "none",
          "&:hover": {
            backgroundColor: "#374151", // un peu plus foncé au hover
          },
        },
      },
    },
  },
});

export { theme };
